Associations


Embed


var postSchema = new mongoose.Schema({
   title: String,
   content: String
});
var Post = mongoose.model("Post", postSchema);

var userSchema = new mongoose.Schema({
   email: String,
   name: String,
   posts: [postSchema]
});
var User = mongoose.model("User", userSchema);


var newUser = new User({
  email: "email",
  name: "name"
});
newUser.posts.push({
  title: "title",
  content: "content"
})




